	  , JM_STATE
	  , PRICE
	  , ID
	  , CATEGORY
	  , RV_STATE
	  , CH_STATE
	  , SV_STATE
	 )
VALUES (
	    #{type}
	  , #{title}
	  , #{content}
	  , NOW()
	  , NOW()
	  , #{location}
	  , #{jmState}
	  , #{price}
	  , #{id}
	  , #{category}
	  , #{rvState}
	  , #{chState}
	  , #{svState}
	 )

mapper.update:
UPDATE MARKET
   SET TYPE = #{type}
	 , TITLE = #{title}
	 , CONTENT = #{content}
	 , LOCATION = #{location}
	 , JM_STATE = #{jmState}
	 , PRICE = #{price}
	 , CATEGORY = #{category}
	 , RV_STATE = #{rvState}
	 , CH_STATE = #{chState}
	 , SV_STATE = #{svState}
	 , UPDATE_DATE = NOW()
 WHERE SERVICE_NO = #{serviceNo}
